CACHE [Activities_ChangeProgramMemberData] SELECT [ActivityId], [ActivityDate], [AttributeDisplayName], [AttributeName], [CampaignId], [LeadId], [NewValue], [ProgramID], [ProgramIDValue] FROM [Activities_ChangeProgramMemberData] WHERE [ActivityDate] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [ActivityDate] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
