CACHE [Activities_ClickLink] SELECT [ActivityId], [ActivityDate], [CampaignId], [ClientIPAddress], [LeadId], [LinkID], [LinkIDValue], [ListId], [QueryParameters], [ReferrerURL], [WebpageID] FROM [Activities_ClickLink] WHERE [ActivityDate] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [ActivityDate] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
