CACHE [Activities_VisitWebpage] SELECT [ActivityId], [ActivityDate], [LeadId], [UserAgent], [WebpageIDValue], [WebpageURL] FROM [Activities_VisitWebpage] WHERE [ActivityDate] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [ActivityDate] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
