CACHE [CampaignMember] SELECT [Id], [SystemModstamp], [CampaignId], [CompanyOrAccount], [ContactId], [CreatedDate], [LastModifiedDate], [LeadId], [LeadOrContactId], [LeadOrContactOwnerId], [Name], [State], [Status], [Title] FROM [CampaignMember] WHERE [SystemModstamp] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [SystemModstamp] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
