CACHE [CampaignMemberStatus] SELECT [Id], [SystemModstamp], [CampaignId], [CreatedById], [CreatedDate], [HasResponded], [IsDefault], [IsDeleted], [Label], [LastModifiedById], [LastModifiedDate], [SortOrder] FROM [CampaignMemberStatus] WHERE [SystemModstamp] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [SystemModstamp] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
