CACHE [OpportunityHistory] SELECT [Id], [SystemModstamp], [Amount], [CloseDate], [CreatedById], [CreatedDate], [ExpectedRevenue], [ForecastCategory], [IsDeleted], [OpportunityId], [Probability], [StageName] FROM [OpportunityHistory] WHERE [SystemModstamp] >= CONCAT(SUBSTRING(DATEADD('d', -1, CURRENT_DATE()), 0, 10), ' 15:00:00') AND [SystemModstamp] < CONCAT(SUBSTRING(CURRENT_DATE(), 0, 10), ' 15:00:00')
